tinymce.PluginManager.add("columns",function(n){function t(n,t){for(var i=!1;!i;){if(t>n.length)break;i=n.charAt(t)==" ";i||t++}return t}n.addButton("columns",{text:"Maak kolommen",icon:!1,onclick:function(){var u=n.getContent().replace(/^\<p\>/,"").replace(/\<\/p\>$/,"");if(u.length>70){var i=u,e=i.length/2,f=t(i,e),o=i.substring(0,f),r=i.substring(f,i.length);r=r.indexOf(" ")==0?r.replace(" ",""):r;n.setContent('<table><tr><td style="width:50%" valign="top">'+o+'<\/td><td style="width:50%" valign="top">'+r+"<\/td><\/tr><\/table>")}}});n.addMenuItem("columns",{text:"Example plugin",context:"tools",onclick:function(){n.windowManager.open({title:"TinyMCE site",url:"http://www.tinymce.com",width:800,height:600,buttons:[{text:"Close",onclick:"close"}]})}})});